local draw = love.graphics.drawlocal rect = love.graphics.rectanglelocal random = math.randomlocal floor = math.floorlocal t = {}math.randomseed(20)local width = 400local height = 400for i = 1, width do	t[i] = {}	for j = 1, height do		t[i][j] = {108,61,37,255}	endendlocal size = 2local function SetPixels(t,x,y)local size = 1 +  floor((y/height)*5)+random(-1,1)	for i = 1, size do		i = i + random(-1,1)		for j = 1, size+random(-1,1) do			if not t[i+x] then				t[i+x] = {}			end			t[i+x][j+y] = nil		end	endendlocal function CountPixels(x,y)	local count = 0	for i = -1, 1 do		for j = -1, 1 do			if t[i+x] and t[i+x][j+y] then				count = count + 1			end		end	end	return countendlocal function test()	for i = 1, width do		for j = 1, height do			if random(100) >  96 then				SetPixels(t,i,j)			end		end	end		for i = 1, width do		for j = 1, height do			if random(5) == 1 then				t[i][j] = nil			end		end	endendlocal function meep()	for i = 1, width do		for j = 1, height do			local count = CountPixels(i,j)			if count < 4 then				t[i][j] = nil			elseif count > 5 then				t[i][j] = {108,61,37,255}			end		end	endendfunction love.mousepressed()for i = 1, width do	t[i] = {}	for j = 1, height do		t[i][j] = {108,61,37,255}	endend	print("test")	test()	meep()endfunction love.keypressed()test()	meep()endfunction love.draw()	for i = 1, width do		for j = 1, height do			if t[i] and t[i][j] then				love.graphics.setColor(unpack(t[i][j]))				rect("fill", i*size, j*size, size-1, size-1)			end		end	end	love.graphics.print(love.timer.getFPS(), 10, 10)end